    fetch("https://api.cloudinary.com/v1_1/simrangumber/image/upload",{
      method:"POST",
      body:data,
    }).then((res)=> res.json())
    .then(data=> {
      setPic(data.url.toString());
      setLoading(false);
    })
    .catch((err)=>{
      
      console.log(err);
      setLoading(false);
    });
  }
  else{
     toast({
       title: "Please Select an Image!",
